import {Route, Routes} from 'react-router-dom'
import React from 'react'

import GlobalFeed from 'pages/globalFeed'
import Article from 'pages/article'
import Authentication from 'pages/authentication'
import TagFeed from 'pages/tagFeed'
import YourFeed from 'pages/yourFeed'

export default () => {
    return (
        <Routes>
            <Route path = '/' element ={<GlobalFeed/>}></Route>
            <Route path = '/feed' element ={<YourFeed/>}></Route>
            <Route path = '/tags/:slug' element ={<TagFeed/>}></Route>
            <Route path = '/articles/:slug' element ={<Article/>}></Route>
            <Route path = '/login' element ={<Authentication/>}></Route>
            <Route path = '/register' element ={<Authentication/>}></Route>
        </Routes>
    )
}